mapper.select:
SELECT u.UserID, u.UserName, u.ProfileImg, a.TotalContribution, t.TeamName FROM Users u JOIN ( SELECT UserID, SUM(ContributionCnt) AS TotalContribution FROM MeetingRoomMembers WHERE IsDelete = false GROUP BY UserID ) a ON u.UserID = a.UserID JOIN Teams t ON u.TeamID = t.TeamID WHERE u.IsDelete = false
AND t.TeamName = #{teamName}
ORDER BY a.TotalContribution DESC LIMIT 5
