mapper.select:
SELECT Status
FROM StageParticipation
WHERE StageID =
#{stageId} AND RoomID = #{roomId}
AND UserID = #{userId}

mapper.update:
UPDATE
StageParticipation
SET Status = 1 ,IdeaID = #{ideaId}
WHERE StageID =
#{stageId} AND RoomID = #{roomId}
AND UserID = #{userId}

mapper.select:
SELECT IdeaReply, IdeaID, UserID, RoomID, ReplyStep, IsDelete, ReplyContent
FROM IdeaReply
WHERE IsDelete = 0 AND IdeaID = #{ideaId}
