mapper.update:
UPDATE Ideas
SET PickNum = PickNum + 1
WHERE Title = #{title}

mapper.select:
SELECT Status
FROM StageParticipation
WHERE StageID = #{stageId} AND IdeaID = #{ideaId} AND RoomID = #{roomId}
AND UserID = #{userId}

mapper.update:
UPDATE StageParticipation
SET Status = 1
WHERE StageID = #{stageId} AND IdeaID = #{ideaId} AND RoomID = #{roomId}
AND UserID = #{userId}
