NotificationID =
#{notificationID}

UPDATE Notifications SET IsDelete = true WHERE
NotificationID =
#{notificationID}

mapper.select:
SELECT *
FROM Notifications
WHERE UserID = #{userId}
AND
RoomID = #{roomId}
AND IsRead = 0
AND IsDelete = 0
AND IdeaID = #{ideaId}
ORDER BY CreatedAt DESC;

